// Create mock translation manager
mockTranslationManager = {
    services: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
};
optimizer = new LanguagePairOptimizer(mockTranslationManager);

tests.code:
expect(optimizer.europeanLanguages).to.be.instanceOf(Set);
expect(optimizer.europeanLanguages.size).to.be.greaterThan(0);
expect(optimizer.europeanLanguages.has('en')).to.be.true;
expect(optimizer.asianLanguages).to.be.instanceOf(Set);
expect(optimizer.asianLanguages.size).to.be.greaterThan(0);
expect(optimizer.asianLanguages.has('ja')).to.be.true;
expect(optimizer.adaptationLanguages).to.be.instanceOf(Set);
expect(optimizer.adaptationLanguages.size).to.be.greaterThan(0);

expect(optimizer.defaultRanking).to.be.an('object');
expect(optimizer.defaultRanking.quality).to.be.an('array');
expect(optimizer.defaultRanking.speed).to.be.an('array');

// English to Spanish is a configured pair
const service = optimizer.getBestServiceForLanguagePair('en', 'es', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select one of the configured services
expect(['deepl', 'gpt4o', 'google', 'azure']).to.include(service);

// Mock a language pair with deepl having highest quality
optimizer.languagePairs.set('en-fr', {
    source: 'en',
    target: 'fr',
    services: {
        deepl: { quality: 0.95, speed: 0.8 },
        gpt4o: { quality: 0.9, speed: 0.7 },
        google: { quality: 0.85, speed: 0.9 },
        azure: { quality: 0.8, speed: 0.85 }
    }
});
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select deepl as it has highest quality
expect(service).to.equal('deepl');

// Mock a language pair with google having highest speed
optimizer.languagePairs.set('en-fr', {
    source: 'en',
    target: 'fr',
    services: {
        deepl: { quality: 0.95, speed: 0.8 },
        gpt4o: { quality: 0.9, speed: 0.7 },
        google: { quality: 0.85, speed: 0.9 }, // Highest speed
        azure: { quality: 0.8, speed: 0.85 }
    }
});
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'speed', // Prioritize speed
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select google as it has highest speed
expect(service).to.equal('google');

// Mock a language pair with deepl having highest quality but unhealthy
optimizer.languagePairs.set('en-fr', {
    source: 'en',
    target: 'fr',
    services: {
        deepl: { quality: 0.95, speed: 0.8 },
        gpt4o: { quality: 0.9, speed: 0.7 },
        google: { quality: 0.85, speed: 0.9 },
        azure: { quality: 0.8, speed: 0.85 }
    }
});
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: false }, // Unhealthy
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select gpt4o as it has second highest quality
expect(service).to.equal('gpt4o');

// Unconfigured language pair
const service = optimizer.getBestServiceForLanguagePair('it', 'ru', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should still select a service
expect(['deepl', 'gpt4o', 'google', 'azure']).to.include(service);

// Both Italian and French are European languages
const service = optimizer.getBestServiceForLanguagePair('it', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select DeepL for European language pair
expect(service).to.equal('deepl');

// Japanese is in the adaptation languages list
const service = optimizer.getBestServiceForLanguagePair('en', 'ja', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    }
});
// Should select GPT-4o for Asian language
expect(service).to.equal('gpt4o');

// Even though deepl might be better for this pair
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    },
    userPreference: 'google' // User prefers Google
});
// Should respect user preference
expect(service).to.equal('google');

// User prefers Google but it's unhealthy
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: false }, // Unhealthy
        azure: { healthy: true }
    },
    userPreference: 'google' // Unhealthy
});
// Should not use user preference as it's unhealthy
expect(service).to.not.equal('google');

const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'speed', // Would normally select Google
    serviceHealth: {
        deepl: { healthy: true },
        gpt4o: { healthy: true },
        google: { healthy: true },
        azure: { healthy: true }
    },
    requiresContext: true // Requires context
});
// Should prioritize a context-aware service (based on defaultRanking.context)
expect(service).to.equal(optimizer.defaultRanking.context[0]);

// All main services unhealthy
const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: false },
        gpt4o: { healthy: false },
        google: { healthy: false },
        azure: { healthy: true } // Only Azure is healthy
    }
});
// Should select the only healthy service
expect(service).to.equal('azure');

// Set up initial quality score
optimizer.languagePairs.set('en-es', {
    source: 'en',
    target: 'es',
    services: {
        deepl: { quality: 0.8, speed: 0.8 }
    }
});
// Update quality score
optimizer.updateQualityScore('deepl', 'en', 'es', 1.0); // Perfect score
// Get updated pair
const pair = optimizer.languagePairs.get('en-es');
// Should update with smoothing (0.8 * 0.9 + 1.0 * 0.1 = 0.82)
expect(pair.services.deepl.quality).to.be.closeTo(0.82, 0.001);

const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {
        deepl: { healthy: false },
        gpt4o: { healthy: false },
        google: { healthy: false },
        azure: { healthy: false }
    }
});
// Should return a default service
expect(service).to.equal('google');

const service = optimizer.getBestServiceForLanguagePair('en', 'fr', {
    priority: 'quality',
    serviceHealth: {}
});
// Should return a service based on default ranking
expect(['deepl', 'gpt4o', 'google', 'azure']).to.include(service);

const service = optimizer.getBestServiceForLanguagePair('en', 'fr');
// Should return a service without throwing
expect(['deepl', 'gpt4o', 'google', 'azure']).to.include(service);

beforeHooks.code:
sandbox = sinon.createSandbox();
cache = new TranslationCache({
    maxCacheSize: 10,
    defaultExpirySeconds: 60
});

tests.code:
const defaultCache = new TranslationCache();
expect(defaultCache.config.maxCacheSize).to.be.greaterThan(0);
expect(defaultCache.config.defaultExpirySeconds).to.be.greaterThan(0);

expect(cache.stats.hits).to.equal(0);
expect(cache.stats.misses).to.equal(0);
expect(cache.stats.totalRequests).to.equal(0);

const key1 = cache.generateKey('Hello', 'en', 'es');
const key2 = cache.generateKey('World', 'en', 'es');
expect(key1).to.not.equal(key2);

const key1 = cache.generateKey('Hello', 'en', 'es');
const key2 = cache.generateKey('Hello', 'en', 'fr');
expect(key1).to.not.equal(key2);

const key1 = cache.generateKey('Hello', 'en', 'es', 'deepl');
const key2 = cache.generateKey('Hello', 'en', 'es', 'google');
expect(key1).to.not.equal(key2);

const key1 = cache.generateKey('Hello', 'en', 'es', 'deepl', { formality: 'formal' });
const key2 = cache.generateKey('Hello', 'en', 'es', 'deepl', { formality: 'informal' });
expect(key1).to.not.equal(key2);

const text = 'Hello world';
const fromLang = 'en';
const toLang = 'es';
const service = 'deepl';
const result = {
    translation: 'Hola mundo',
    confidence: 0.95
};
await cache.set(text, fromLang, toLang, service, result);
const cached = await cache.get(text, fromLang, toLang, service);
expect(cached).to.deep.equal(result);

const text = 'Hello world';
const fromLang = 'en';
const toLang = 'es';
const service = 'deepl';
const result = {
    translation: 'Hola mundo',
    confidence: 0.95
};
await cache.set(text, fromLang, toLang, service, result);
// Should be retrievable with 'any' service
const cached = await cache.get(text, fromLang, toLang, 'any');
expect(cached).to.deep.equal(result);

// First access (miss)
await cache.get('Not in cache', 'en', 'es', 'deepl');
expect(cache.stats.totalRequests).to.equal(1);
expect(cache.stats.misses).to.equal(1);
expect(cache.stats.hits).to.equal(0);
// Add to cache
await cache.set('Hello', 'en', 'es', 'deepl', { translation: 'Hola' });
// Second access (hit)
await cache.get('Hello', 'en', 'es', 'deepl');
expect(cache.stats.totalRequests).to.equal(2);
expect(cache.stats.misses).to.equal(1);
expect(cache.stats.hits).to.equal(1);

// Set with a very short expiry (1ms)
await cache.set('Hello', 'en', 'es', 'deepl', { translation: 'Hola' }, 0.001);
// Wait for expiry
await new Promise(resolve => setTimeout(resolve, 10));
// Should return null after expiry
const cached = await cache.get('Hello', 'en', 'es', 'deepl');
expect(cached).to.be.null;
expect(cache.stats.misses).to.equal(1);

// Add some entries with very short expiry
await cache.set('Entry1', 'en', 'es', 'deepl', { translation: 'Test1' }, 0.001);
await cache.set('Entry2', 'en', 'es', 'deepl', { translation: 'Test2' }, 0.001);
// Wait for expiry
await new Promise(resolve => setTimeout(resolve, 10));
// Prune cache
cache.pruneCache();
// Should be removed
const cached1 = await cache.get('Entry1', 'en', 'es', 'deepl');
const cached2 = await cache.get('Entry2', 'en', 'es', 'deepl');
expect(cached1).to.be.null;
expect(cached2).to.be.null;

// Cache is configured with maxCacheSize of 10
// Add 15 entries (exceeds maxCacheSize)
for (let i = 0; i < 15; i++) {
    await cache.set(`Entry${i}`, 'en', 'es', 'deepl', { translation: `Test${i}` });
}
// Cache should prune automatically
expect(cache.cache.size).to.be.at.most(10);

// Add some cache hits and misses
await cache.get('Miss1', 'en', 'es', 'deepl');
await cache.get('Miss2', 'en', 'es', 'deepl');
await cache.set('Hit1', 'en', 'es', 'deepl', { translation: 'Test1' });
await cache.get('Hit1', 'en', 'es', 'deepl');
await cache.set('Hit2', 'en', 'es', 'deepl', { translation: 'Test2' });
await cache.get('Hit2', 'en', 'es', 'deepl');
const stats = cache.getStats();
expect(stats.hits).to.equal(2);
expect(stats.misses).to.equal(2);
expect(stats.totalRequests).to.equal(4);
expect(stats.size).to.be.greaterThan(0);
expect(stats.hitRate).to.equal(0.5); // 2 hits out of 4 requests

// Add some entries
await cache.set('Entry1', 'en', 'es', 'deepl', { translation: 'Test1' });
await cache.set('Entry2', 'en', 'es', 'deepl', { translation: 'Test2' });
// Verify they exist
expect(await cache.get('Entry1', 'en', 'es', 'deepl')).to.not.be.null;
expect(await cache.get('Entry2', 'en', 'es', 'deepl')).to.not.be.null;
// Clear cache
await cache.clear();
// Verify they're gone
expect(await cache.get('Entry1', 'en', 'es', 'deepl')).to.be.null;
expect(await cache.get('Entry2', 'en', 'es', 'deepl')).to.be.null;
expect(cache.cache.size).to.equal(0);

await cache.set('', 'en', 'es', 'deepl', { translation: '' });
const cached = await cache.get('', 'en', 'es', 'deepl');
expect(cached).to.deep.equal({ translation: '' });

const text = 'Special: !@#$%^&*()_+{}[]|\\:;"\'<>,.?/';
const result = { translation: 'Translated special chars' };
await cache.set(text, 'en', 'es', 'deepl', result);
const cached = await cache.get(text, 'en', 'es', 'deepl');
expect(cached).to.deep.equal(result);

// Generate a long string
const longText = 'A'.repeat(10000);
const result = { translation: 'Long text translation' };
await cache.set(longText, 'en', 'es', 'deepl', result);
const cached = await cache.get(longText, 'en', 'es', 'deepl');
expect(cached).to.deep.equal(result);

expect(formatTextForDisplay(null)).to.equal('');
expect(formatTextForDisplay(undefined)).to.equal('');
expect(formatTextForDisplay(123)).to.equal('');
expect(formatTextForDisplay({})).to.equal('');

const longText = 'a'.repeat(150);
const result = formatTextForDisplay(longText, 100);
expect(result).to.have.lengthOf(103); // 100 + '...'
expect(result).to.match(/\.\.\.$/);

expect(capitalize(null)).to.equal('');
expect(capitalize(undefined)).to.equal('');
expect(capitalize(123)).to.equal('');

expect(safeJsonParse('not json', { default: 'value' }))
  .to.deep.equal({ default: 'value' });
expect(safeJsonParse('', 'fallback')).to.equal('fallback');

const mock = createMockAudioContext();

expect(mock).to.have.property('suspend').that.is.a('function');
expect(mock).to.have.property('resume').that.is.a('function');
expect(mock).to.have.property('close').that.is.a('function');
expect(mock).to.have.property('destination');
expect(mock).to.have.property('createMediaStreamSource').that.is.a('function');

const mock = createMockAudioContext();
await mock.suspend();

expect(mock.suspend).to.have.been.calledOnce;

const mock = createMockMediaStream();

expect(mock).to.have.property('getTracks').that.is.a('function');
expect(mock).to.have.property('getAudioTracks').that.is.a('function');
expect(mock).to.have.property('getVideoTracks').that.is.a('function');
expect(mock).to.have.property('addTrack').that.is.a('function');
expect(mock).to.have.property('removeTrack').that.is.a('function');

const callback = () => {};
const observer = new MockResizeObserver(callback);

expect(observer).to.have.property('observe').that.is.a('function');
expect(observer).to.have.property('unobserve').that.is.a('function');
expect(observer).to.have.property('disconnect').that.is.a('function');

const MockComponent = createComponentMock('TestComponent');
const element = MockComponent.TestComponent({ children: 'test' });

expect(element.type).to.equal('TestComponent');
expect(element.props.children).to.equal('test');

const { promise, resolve } = createControllablePromise();
const testValue = 'test value';

setTimeout(() => resolve(testValue), 100);

const result = await promise;
expect(result).to.equal(testValue);

let value = false;
setTimeout(() => { value = true; }, 50);

await waitFor(() => value === true, 200);
expect(value).to.be.true;

try {
  await waitFor(() => false, 50);
  throw new Error('Should have timed out');
} catch (error) {
  expect(error.message).to.include('Timeout');
}

const clock = useFakeTimers();
const callback = sinon.stub();

setTimeout(callback, 1000);
clock.tick(1000);

expect(callback).to.have.been.calledOnce;
clock.restore();

afterHooks.code:
if (global.fetch && global.fetch.restore) {
  global.fetch.restore();
} else {
  delete global.fetch;
}

tests.code:
const testData = { id: 1, name: 'Test' };
const mock = mockFetch(testData);

const response = await fetch('https://api.example.com/test');
const data = await response.json();

expect(mock).to.have.been.calledWith('https://api.example.com/test');
expect(response.ok).to.be.true;
expect(data).to.deep.equal(testData);

const mock = mockFetch(null, { status: 404, ok: false, statusText: 'Not Found' });

const response = await fetch('https://api.example.com/not-found');

expect(mock).to.have.been.calledWith('https://api.example.com/not-found');
expect(response.ok).to.be.false;
expect(response.status).to.equal(404);
